import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const BookDetails = (props) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define an async function to fetch data
    const fetchData = async () => {
      try {
        const response = await fetch("https://freetestapi.com/api/v1/books"); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const result = await response.json(); // Parse the JSON data
        setData(result); // Update the state with fetched data
      } catch (error) {
        setError(error.message); // Set error if fetch fails
      } finally {
        setLoading(false); // Set loading to false after data is fetched
      }
    };

    fetchData(); // Call the fetch function when the component mounts
  }, []);

  const { id } = useParams();
  console.log(id);
  console.log(typeof id);
  const bookId = parseInt(id);
  console.log(typeof bookId);
  const filteredBook = data.filter((each) => each.id === bookId);
  console.log(filteredBook);
  const reqObj = filteredBook[0];

  console.log(reqObj);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div class="book-details-container">
      <div class="book-image">
        <img src={reqObj.cover_image} alt="Book Cover" />
      </div>
      <div class="book-info">
        <h1 class="book-title">{reqObj.title}</h1>
        <h3 class="book-author">by {reqObj.author}</h3>
        <p class="book-description">{reqObj.description}</p>
        <div class="book-details">
          <p>
            <strong>Genre:</strong> {reqObj.genre[0]}
          </p>
          <p>
            <strong>Published:</strong> {reqObj.publication_year}
          </p>
          <p>
            <strong>ISBN:</strong> 978-0743273565
          </p>
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
