import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const DataFetchingComponent = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define an async function to fetch data
    const fetchData = async () => {
      try {
        const response = await fetch("https://freetestapi.com/api/v1/books"); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const result = await response.json(); // Parse the JSON data
        setData(result); // Update the state with fetched data
      } catch (error) {
        setError(error.message); // Set error if fetch fails
      }
    };

    fetchData(); // Call the fetch function when the component mounts
  }, []);

  const { id } = useParams();
  const filteredBook = data.filter((each) => each.id === parseInt(id));
  const { title, author, cover_image, description, genre, publication_year } =
    filteredBook[0];

  return (
    <div class="book-details-container">
      <div class="book-image">
        <img src={cover_image} alt="Book Cover" />
      </div>
      <div class="book-info">
        <h1 class="book-title">The Great Gatsby</h1>
        <h3 class="book-author">by F. Scott Fitzgerald</h3>
        <p class="book-description">
          A classic novel set in the Roaring Twenties, "The Great Gatsby" tells
          the story of the mysterious millionaire Jay Gatsby, his love for Daisy
          Buchanan, and the dark side of the American Dream.
        </p>
        <div class="book-details">
          <p>
            <strong>Genre:</strong> Fiction, Classics
          </p>
          <p>
            <strong>Published:</strong> 1925
          </p>
          <p>
            <strong>ISBN:</strong> 978-0743273565
          </p>
        </div>
        <button class="btn-favorite">Add to Favorites</button>
      </div>
    </div>
  );
};

export default DataFetchingComponent;
