import { Component } from "react";

import "./index.css";

import EachBook from "../EachBook";

class Home extends Component {
  state = { books: [], likedBooks: [] };

  componentDidMount() {
    this.getBooks();
  }

  getBooks = async () => {
    const url = "https://freetestapi.com/api/v1/books";
    const options = {
      method: "GET",
    };
    const response = await fetch(url, options);
    const data = await response.json();
    const modifiedData = data.map((each) => ({
      ...each,
      isLiked: false,
    }));

    this.setState({ books: modifiedData });
  };

  onClickLikeButton = (id) => {
    const { books } = this.state;

    const updatedBooks = books.map((each) => {
      if (each.id === id) {
        return { ...each, isLiked: !each.isLiked };
      }
      return each;
    });

    const filterlikedBooks = updatedBooks.filter(
      (each) => each.isLiked === true
    );

    this.setState({ books: updatedBooks, likedBooks: filterlikedBooks });
  };

  render() {
    const { books } = this.state;
    return (
      <div className="Home-Page">
        <div className="header">
          <p className="logo">Logo</p>
        </div>
        <div className="container-div">
          <ul className="container">
            {books.map((each) => (
              <EachBook
                key={each.id}
                details={each}
                onClickLikeButton={this.onClickLikeButton}
              />
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default Home;
